import 'dart:io';import 'package:contect_diary_1/Moduls/Screens/Add_Contect_Screen/Model/add_contect_model.dart';import 'package:contect_diary_1/Moduls/Screens/Add_Contect_Screen/Model/add_contect_model_contect_detail.dart';import 'package:flutter/cupertino.dart';import 'package:image_picker/image_picker.dart';class ContectProvider extends ChangeNotifier {  List<Contect> allContect = [];  // ImagePicker picker = ImagePicker();  Add_Contect_Model cI = Add_Contect_Model(currentIndex: 0);  addContect({required Contect contect_details}) {    allContect.add(contect_details);    notifyListeners();  }  removeContect({required Contect contect}) {    allContect.remove(contect);    notifyListeners();  }  stepContinue() {    if (0 <= cI.currentIndex && cI.currentIndex < 3) {      cI.currentIndex++;      notifyListeners();    }  }  stepCancel() {    if (cI.currentIndex > 0) {      cI.currentIndex--;      notifyListeners();    }  }  // void pickImage() async {  //   XFile? file = await picker.pickImage(source: ImageSource.gallery);  //   if (file != null) {  //     FileImage(File(picker!.path));  //   }  // }}