import 'package:contect_diary_1/Moduls/Screens/Add_Contect_Screen/Model/add_contect_model_contect_detail.dart';import 'package:contect_diary_1/Moduls/Screens/Add_Contect_Screen/Providers/contect_provider.dart';import 'package:flutter/material.dart';import 'package:provider/provider.dart';import 'package:share_plus/share_plus.dart';import 'package:url_launcher/url_launcher.dart';import '../../../../Utils/Global.dart';class ContectDetailScreen extends StatelessWidget {  const ContectDetailScreen({    super.key,  });  @override  Widget build(BuildContext context) {    Contect data = ModalRoute.of(context)?.settings.arguments as Contect;    return Scaffold(      appBar: AppBar(        title: const Text("Contect Detail"),        centerTitle: true,        actions: [          IconButton(            onPressed: () {              Provider.of<ContectProvider>(context, listen: false)                  .removeContect(                      contect: Contect(                          name: data.name,                          email: data.email,                          phone: data.phone));              Navigator.pushReplacementNamed(context, 'home');            },            icon: const Icon(              Icons.delete_forever_outlined,              size: 30,              color: Colors.black,            ),          ),        ],      ),      body: Center(        child: Column(          children: [            const SizedBox(              height: 90,            ),            const CircleAvatar(              backgroundColor: Colors.orange,              radius: 110,              child: CircleAvatar(                radius: 100,                foregroundImage: AssetImage(                  "lib/Utils/Assets/contact-icon"                  ".png",                ),              ),            ),            Text(              data.name,              style: const TextStyle(                fontSize: 25,              ),            ),            Text(              '+91 ${data.phone}',              style: const TextStyle(                fontSize: 22,              ),            ),            Text(              data.email,              style: const TextStyle(                fontSize: 16,              ),            ),            const Divider(),            const SizedBox(              height: 20,            ),            Row(              mainAxisAlignment: MainAxisAlignment.spaceEvenly,              children: [                FloatingActionButton(                  heroTag: "btn1",                  backgroundColor: Colors.green.withOpacity(0.6),                  onPressed: () {                    launchUrl(                      Uri(scheme: "tel", path: "+91 ${data.phone}"),                    );                  },                  child: const Icon(                    Icons.call,                    color: Colors.black,                  ),                ),                FloatingActionButton(                  heroTag: "btn2",                  backgroundColor: Colors.blue.withOpacity(0.6),                  onPressed: () {                    launchUrl(                      Uri(scheme: "sms", path: "+91 ${data.phone}"),                    );                  },                  child: const Icon(                    Icons.sms,                    color: Colors.black,                  ),                ),                FloatingActionButton(                  heroTag: "btn3",                  backgroundColor: Colors.red.withOpacity(0.6),                  onPressed: () {                    launchUrl(                      Uri(scheme: "mailto", path: data.email),                    );                  },                  child: const Icon(                    Icons.email,                    color: Colors.black,                  ),                ),                FloatingActionButton(                  heroTag: "btn4",                  backgroundColor: Colors.yellow.withOpacity(0.6),                  onPressed: () {                    Share.share("${data.name}\n"                        "+91 ${data.phone}\n"                        "${data.email}");                  },                  child: const Icon(                    Icons.share,                    color: Colors.black,                  ),                ),              ],            ),            const SizedBox(              height: 20,            ),            const Divider(),          ],        ),      ),    );  }}