import 'dart:developer';import 'package:contect_diary_1/Moduls/Screens/Contect_Detail/Views/contect_datail.dart';import 'package:contect_diary_1/Utils/Global.dart';import 'package:flutter/material.dart';import 'package:provider/provider.dart';import 'package:url_launcher/url_launcher.dart';import '../../../../Utils/Themes/app_theme.dart';import '../../Add_Contect_Screen/Model/add_contect_model_contect_detail.dart';import '../../Add_Contect_Screen/Providers/contect_provider.dart';class HomeScreen extends StatelessWidget {  const HomeScreen({super.key});  @override  Widget build(BuildContext context) {    List<Contect> allContact =        Provider.of<ContectProvider>(context, listen: false).allContect;    return Scaffold(      floatingActionButton: FloatingActionButton(        onPressed: () {          Navigator.pushNamed(context, 'add_contect');        },        child: const Icon(          Icons.add,        ),      ),      appBar: AppBar(        title: const Text("Welcome"),        centerTitle: true,      ),      body: (Provider.of<ContectProvider>(context, listen: false)              .allContect              .isNotEmpty)          ? ListView.builder(              itemCount: allContact.length,              itemBuilder: (ctx, i) => Padding(                padding: const EdgeInsets.symmetric(horizontal: 10),                child: Card(                  child: ListTile(                    onTap: () {                      Navigator.pushNamed(context, 'contect_detail',                          arguments: allContact[i]);                      // Navigator.push(                      //   context,                      //   MaterialPageRoute(                      //     builder: (context) => ContectDetailScreen(index: i),                      //   ),                      // );                    },                    leading: const CircleAvatar(                      foregroundImage: NetworkImage(                        'https://icons.iconarchive.com/icons/graphicloads/100-flat/256/contact-icon.png',                      ),                      radius: 30,                    ),                    title: Text(allContact[i].name),                    subtitle: Text("+91 ${allContact[i].phone}"),                    // trailing: IconButton(                    //   onPressed: () {                    //     launchUrl(                    //       Uri(scheme: "tel", path: "+91 ${Global.phone}"),                    //     );                    //   },                    //   icon: const Icon(                    //     Icons.call,                    //   ),                    // ),                    trailing: IconButton(                      onPressed: () {                        log("${allContact[i].name}");                        log("${allContact[i].email}");                        log("${allContact[i].phone}");                        Provider.of<ContectProvider>(context, listen: false)                            .removeContect(                                contect: Contect(                                    name: allContact[i].name,                                    email: allContact[i].email,                                    phone: allContact[i].phone));                      },                      icon: Icon(Icons.delete_forever_outlined),                    ),                  ),                ),              ),            )          : Center(              child: Container(                height: MediaQuery.of(context).size.height * 0.3,                width: double.infinity,                decoration: const BoxDecoration(                  image: DecorationImage(                    image: AssetImage(                      'lib/Utils/Assets/no_contect_data.png',                    ),                    fit: BoxFit.cover,                  ),                ),              ),            ),    );  }}