import 'dart:async';import 'package:flutter/material.dart';class Splash_Screen extends StatelessWidget {  const Splash_Screen({super.key});  @override  Widget build(BuildContext context) {    double h = MediaQuery.of(context).size.height;    Timer(const Duration(seconds: 3), () {      Navigator.pushReplacementNamed(context, 'home');    });    return Scaffold(      body: Center(        child: Column(          mainAxisAlignment: MainAxisAlignment.center,          children: [            Padding(              padding: const EdgeInsets.all(10),              child: Image.asset(                'lib/Utils/Assets/splash.gif',                color: Colors.red,              ),            ),          ],        ),      ),    );  }}